% Root finding bisection method % When given function, find the roots % June 11, 2018 David Akinyemiclear alla0 = 0; % Declare left boundb0 = 3; % Declare right boundepsilon = 1e-5; % Declare variable for future stopping pointf = @(x) x^2  - 3;% Define function i=1; % Define starting variablexn(i) = .5 * (a0 + b0); % Define equation used to estimate midpoint/rootscounter = 0;while abs(f(xn(i))) > epsilon %Bisection method   i=i + 1;   xni = xn(i -1);   if f(xni) * f(a0) < 0      b0 = xni;   else       a0=xni;   endif    xn(i) = .5 * (a0 + b0);   counter = counter + 1;   [xn(i)  f(xn(i))  counter] endwhile