%David Akinyemi % July 17, 2018% When given certain characteristics simulates bridge behavior, no windfunction [y,v] = BridgeY(a,b,alpha,lambda,mu,y0,v0,t,n,w,L)y = zeros(n+1,1); %Vertical position solution vectorv = zeros(n+1,1); %Vertical velocity solution vectorh = t/n;y(1) = y0;v(1) = v0;xR(1) = 0;yR(1) = y0;xL(1) = 0;yL(1) = y0;for i = 1:L/2    xR(i+1) = xR(i) + 1;    yR(i+1) = ((4*w)/L^2) * xR(i).^2 + y0;     endforfor i = 1:L/2    xL(i+1) = xL(i) - 1;    yL(i+1) = ((4*w)/L^2) * xL(i).^2 + y0;     endfor for i = 1:n;  if y(i) < 0    hy = a * y(i);  else    hy = b * y(i);  endif   acc = -hy - (alpha * v(i)) - 9.81 + (lambda *sin(mu*t));  v(i+1) = v(i) + h*acc;  y(i+1) = y(i) + h*v(i);endfor[ y(i+1) v(i+1) acc]z=0:h:t;%Plotfigure(1)plot(z,y,'b-')title('Center of Tacoma Bridge')xlabel('Time'); ylabel('Vertical Position'); figure(2)plot(xR,yR,'k-',xL,yL,'r--')title('Shape of Tacoma Bridge')xlabel('x'); ylabel('y'); 