function [z,k] = Coupled3(x1,x2,k,m,n)w1 = sqrt(k/m)w2 = sqrt((3*k)/m)B= (k+k)/m;a= k/m;A= [-B  a; a  -B];[v,d]=eig(A);             %Find Eigenvalues and vectors.w= [w2; w1]                 %Get frequenciesx0=[x1 x2]'                %Initial positionC=inv(v)*x0             %Find unknown coefficientsg=diag(C)t=0:0.2:n;                                                                   x=v*g*cos(w*t);       %Calculate poisition%Plotplot(t,x)xlabel('Time'); ylabel('Output'); 