%David Akinyemi 27 June 2018%When given initial position, velocity, time, spring constant and mass, caluculates accelerationstlen = 12;n = 1000;[x,v,a] = SpringRunge(0,5,tlen,n,15,3);h= tlen/n;t = 0:h:tlen;subplot(2,2,1), plot(t,x)xlabel('Time')ylabel('Position')subplot(2,2,2), plot(t,v)xlabel('Time')ylabel('Velocity')subplot(2,2,3), plot(t,a)xlabel('Time')ylabel('Acceleration')subplot(2,2,4), plot(x,v)xlabel('Position')ylabel('Velocity')