% David Akinyemi June 25 2018% Compute acceleration and force when given initial x and v function [x,v,a] = SpringRunge(x0,v0,t,n,k,m)x = zeros(n+1,1); %Position solution vectorv = zeros(n+1,1); %Velocity solution vectora = zeros(n+1,1); %Acceleration solution vectorh=t/n;x(1) = x0;v(1) = v0;for i = 1:n    a(i+1) = (-k/m) * x(i);    vk1 = a(i+1);    vk2 = a(i+1)+((h/2)*vk1);    vk3 = a(i+1)+((h/2)*vk2);    vk4 = a(i+1)+(h*vk3);    v(i+1) = v(i) + ((h/6)*(vk1+(2*vk2)+(2*vk3)+vk4));    xk1 = v(i+1);    xk2 = v(i+1)+((h/2)*xk1);    xk3 = v(i+1)+((h/2)*xk2);    xk4 = v(i+1)+(h*xk3);    x(i+1) = x(i) + ((h/6)*(xk1+(2*xk2)+(2*xk3)+xk4));   endfor[x(i+1) v(i+1) a(i+1)]