% David Akinyemi 28 June 2018% Coupled Oscillators % When given spring constants calculates frequency of both massesfunction [x,y,vx,vy,ax,ay] = TEST(x0,y0,z0,vx0,vy0,vz0,k,m,t,n)x = zeros(n+1,1); %Position1 solution vectory = zeros(n+1,1); %Position2 solution vectorz = zeros(n+1,1); %Position3 solution vectorvx = zeros(n+1,1); %Velocity1 solution vectorvy = zeros(n+1,1); %Velocity2 solution vectorvz = zeros(n+1,1); %Velocity3 solution vectorax = zeros(n+1,1); %Acceleration1 solution vectoray = zeros(n+1,1); %Acceleration2 solution vectoraz = zeros(n+1,1); %Acceleration3 solution vectorh=t/n;x(1) = x0;y(1) = y0;z(1) = z0;vx(1) = vx0;vy(1) = vy0;vz(1) = vz0;for i = 1:n    ax(i) = k/m * (-2*x(i) + y(i));    ay(i) = k/m * (x(i) + (-2 * y(i)) + z(i));    az(i) = k/m * (y(i) + (-2 * z(i)));    vx(i+1) = vx(i) +h*ax(i);    vy(i+1) = vy(i) +h*ay(i);    vz(i+1) = vz(i) +h*az(i);    x(i+1) = x(i) + h*vx(i+1);    y(i+1) = y(i) + h*vy(i+1);    z(i+1) = z(i) + h*vz(i+1);    endforp=0:h:t;%Plotfigure(5)comet(p,x,0.001);comet(p,y,0.001);comet(p,z,0.001);plot(p,x,'k-',p,y,'r--',p,z,'b-')xlabel('Time'); ylabel('Output'); 